'use client'
import { useState, useEffect } from 'react'
import { motion } from 'framer-motion'
import { PlusCircle, Trash2, FolderX } from 'lucide-react'

export default function TodoList() {
  const [tasks, setTasks] = useState<string[]>([])
  const [input, setInput] = useState('')

  useEffect(() => {
    const saved = localStorage.getItem('tasks')
    if (saved) setTasks(JSON.parse(saved))
  }, [])

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks))
  }, [tasks])

  const addTask = () => {
    if (!input.trim()) return
    setTasks([...tasks, input])
    setInput('')
  }

  const deleteTask = (i: number) => setTasks(tasks.filter((_, index) => index !== i))
  const clearTasks = () => setTasks([])

  return (
    <section className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white flex items-center justify-center p-6">
      <div className="w-full max-w-lg bg-gray-800/60 backdrop-blur-xl border border-gray-700 shadow-2xl rounded-2xl p-8">
        <h1 className="text-3xl font-bold mb-2 text-center tracking-tight">Ma To-Do List</h1>
        <p className="text-gray-400 text-center mb-6">Total des t√¢ches : {tasks.length}</p>
        <div className="flex gap-2 mb-6">
          <input type="text" value={input} onChange={(e) => setInput(e.target.value)} placeholder="Entrer une nouvelle t√¢che" className="flex-1 bg-gray-700 text-white placeholder-gray-400 border border-gray-600 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500" />
          <button onClick={addTask} className="flex items-center gap-1 bg-indigo-600 hover:bg-indigo-700 transition rounded-lg px-4 py-2 font-semibold"><PlusCircle size={18}/> Ajouter</button>
        </div>
        {tasks.length > 0 && (
          <button onClick={clearTasks} className="flex items-center gap-1 bg-red-600 hover:bg-red-700 transition rounded-lg px-4 py-2 font-semibold mb-6 mx-auto"><FolderX size={18}/> Tout effacer</button>
        )}
        {tasks.length === 0 ? (
          <p className="text-gray-400 text-center italic">Aucune t√¢che pour le moment.</p>
        ) : (
          <ul className="space-y-3">
            {tasks.map((t, i) => (
              <motion.li key={i} initial={{opacity:0, y:10}} animate={{opacity:1, y:0}} exit={{opacity:0, y:-10}} className="flex justify-between items-center p-4 rounded-lg border bg-gray-700/50 border-gray-600 hover:bg-gray-700/70 transition">
                <span className="text-lg text-gray-100">{t}</span>
                <button onClick={() => deleteTask(i)} className="text-red-400 hover:text-red-500 transition"><Trash2 size={20}/></button>
              </motion.li>
            ))}
          </ul>
        )}
      </div>
    </section>
  )
}

